function getKeyword(ciphertext, keyLength) {
  const charCode = c => c.charCodeAt(0) - 65;
  const toChar = n => String.fromCharCode((n % 26) + 65);

  const englishFreq = [
    0.08167, 0.01492, 0.02782, 0.04253, 0.12702, 0.02228, 0.02015,
    0.06094, 0.06966, 0.00153, 0.00772, 0.04025, 0.02406, 0.06749,
    0.07507, 0.01929, 0.00095, 0.05987, 0.06327, 0.09056, 0.02758,
    0.00978, 0.02360, 0.00150, 0.01974, 0.00074
  ];

  let key = "";

  for (let i = 0; i < keyLength; i++) {
    let letters = [];
    for (let j = i; j < ciphertext.length; j += keyLength) {
      letters.push(ciphertext[j]);
    }

    let bestScore = -Infinity;
    let bestShift = 0;
    for (let shift = 0; shift < 26; shift++) {
      let freq = Array(26).fill(0);
      for (let c of letters) {
        let idx = (charCode(c) - shift + 26) % 26;
        freq[idx]++;
      }
      let total = letters.length;
      let normFreq = freq.map(f => f / total);

      let score = normFreq.reduce((sum, f, idx) => sum + f * englishFreq[idx], 0);

      if (score > bestScore) {
        bestScore = score;
        bestShift = shift;
      }
    }
    key += toChar(bestShift);
  }

  return key;
}



